import React, { useState } from 'react';
import { createAppointment } from '../api';

const DoctorCard = ({ doctor, user }) => {
    const [duration, setDuration] = useState('');

    const handleAppointment = async () => {
        if (!user) {
            alert('Por favor inicie sesi칩n para pedir una cita');
            return;
        }
        try {
            await createAppointment(doctor.id, user.username, duration);
            alert('Cita creada correctamente');
        } catch (error) {
            alert('Error al crear la cita');
        }
    };

    return (
        <div className="doctor-card">
            <h3>{doctor.first_name} {doctor.last_name}</h3>
            <p>Edad: {doctor.age}</p>
            <p>Sexo: {doctor.gender}</p>
            <p>A침os de experiencia: {doctor.years_of_experience}</p>
            <input 
                type="text" 
                placeholder="Duraci칩n de la cita" 
                value={duration} 
                onChange={(e) => setDuration(e.target.value)} 
            />
            <button onClick={handleAppointment}>Pedir Cita</button>
        </div>
    );
};

export default DoctorCard;
